const Temp = msg.payload;
const override = global.get("AutoManual_switch") || 0;
const fanstate = global.get("FanState");
if (override != 1) {
    if (Temp <= 24 && fanstate !== 0) {
        msg.payload = 0;
        global.set("FanState", msg.payload);
        return msg;
    } else if (Temp >= 27 && fanstate != 1) {
        msg.payload = 1;
        global.set("FanState", msg.payload);
        return msg;
    } else {
        return null;
    }
}

const override = global.get("AutoManual_switch") || 0;
if(override === 0){
    msg.payload = 1;
    return msg;
} else {
    return null;
}
