var topic = "/oneM2M/req/aqm/id-in/json"
var room = msg.payload.to;
var dest = msg.payload.fr;
var co2_value;

// CO2 Threshold on/off
var threshold_on = 3000;
var threshold_off = 2000;

// Automatic Mode on
var auto_mode = true;

msg.topic = topic;

// Actuator on
var msg_actuator_on = {
    fr: `${room}`,
    to: `cse-in/airQualityMonitoring/${room}/actuator/status`,
    op: 3,
    rvi: "3",
    rqi: "1234562",
    pc: {
        'cod\:binSh': {
            powSe: true
        }
    },
    ty: 28
}

// Actuator off
var msg_actuator_off = {
    fr: `${room}`,
    to: `cse-in/airQualityMonitoring/${room}/actuator/status`,
    op: 3,
    rvi: "3",
    rqi: "1234562",
    pc: {
        'cod\:binSh': {
            powSe: false
        }
    },
    ty: 28
}

if (msg.payload.threshold_on) {
    threshold_on = msg.payload.threshold_on;
    return 0;
}

if (msg.payload.threshold_off) {
    threshold_off = msg.payload.threshold_off;
    return 0;
}

if (msg.payload.auto_off) {
    auto_mode = false;
    return 0;
}

if (room != dest) {
    if (auto_mode === true) {
        if (binSh) {
            msg.payload = {};
            msg.topic = {};
        }
        if (coSLd) {
            msg.payload = {};
            msg.topic = {};
        }
        if (aiQSr) {
            co2_value = msg.payload.aiQSr.co2;
            msg.payload = {};
            if (co2_value >= threshold_on) {
                msg.payload = msg_actuator_on;
            }   
            if (co2_value <= threshold_off) {
                msg.payload = msg_actuator_off;
            }
        }
    } 
    else {
            delete msg.payload;
            delete msg.topic;
    }
}

return msg;

